import React, {useState, useEffect} from "react";
import {Link} from "react-router-dom";
import Navbar from "./Navbar";
import "./FarmList.css";
import Farm from "./Farm";

function FarmList() {
  const [farmsList, setFarmsList] = useState({
    amjad: {
      name: "amjad",
      fields: [123123, 123123, "sdfsdfsdfsd"],
      open: true,
    },
    asdas: {
      name: "asdas",
      fields: [],
      open: true,
    },
    asklndkasjh: {
      name: "asklndkasjh",
      fields: [],
      open: true,
    },
  });

  useEffect(() => {
    console.log("rerendering");
  });

  function toggleFarm(nameid) {
    console.log("we running;");
    let farmCopy = {open: !farmsList[namedid].open, ...farmsList[namedid]};
    farmsList[nameid] = {...farmsList[nameid], open: !farmsList[nameid].open};
  }

  return (
    <div className="farmList">
      <Navbar />
      <h2>List of Farms</h2>
      <div className="farmList-list">
        {farmsList.map((farm) => (
          <div>
            <Farm
              nameid={farm.name}
              fields={farm.fields}
              open={farm.open}
              toggleFunc={toggleFarm}
            />
            <br />
          </div>
        ))}
      </div>
    </div>
  );
}

export default FarmList;
