import React, {useState} from "react";
import "./Login.css";

function Login(props) {
  const adminUsr = {
    email: "1140@manna.com",
    password: "123456",
  };

  const [details, setDetails] = useState({email: "", password: ""});
  const [error, setError] = useState("");

  useEffect(() => {
    document.body.style.backgroundImage =
      "linear-gradient(to right,rgba(0, 115, 186, 0.907) 30%,#16fff7 120%)";
  }, []);
  const logIn = (details) => {
    console.log(details);
    if (
      (details.email == adminUsr.email &&
        details.password == adminUsr.password) ||
      details.email == "bader@a"
    ) {
      console.log("logged in");
      props.history.push("/farmlist");
    } else {
      setError("Email/Password do not match");
    }
  };

  const submitHandler = (event) => {
    event.preventDefault();
    logIn(details);
  };
  return (
    <div className="loginBody">
      <h1 className="title">MannaViewer</h1>
      <form onSubmit={submitHandler}>
        <div className="form-inner">
          <h2>Login</h2>
          <div className="form-group">
            <label htmlFor="email">Email: </label>
            <input
              type="email"
              name="email"
              id="email"
              placeholder="Enter email"
              onChange={(event) =>
                setDetails({...details, email: event.target.value})
              }
              value={details.email}
            />
          </div>
          <div className="form-group">
            <label htmlFor="password">Password: </label>
            <input
              type="password"
              name="password"
              id="password"
              placeholder="Enter password"
              onChange={(event) =>
                setDetails({...details, password: event.target.value})
              }
              value={details.password}
            />
          </div>
          {error !== "" ? (
            <div className="error">{error}</div>
          ) : (
            <div className="error"></div>
          )}
          <input type="submit" value="Login" />
        </div>
      </form>
    </div>
  );
}

export default Login;
