import React, {useState, useEffect} from "react";
import Navbar from "./Navbar";
import "./FarmList.css";
import Farm from "./Farm";

function FarmList(props) {
  const [farmsList, setFarmsList] = useState([]);
  //       [
  //     {
  //       id: 123123,
  //       name: "amjad",
  //       fields: [123123, 123123, "sdfsdfsdfsd", "test,", "testasd"],
  //       open: true,
  //     },
  //     {
  //       name: "asdas",
  //       fields: ["test", "12s"],
  //       open: true,
  //     },
  //     {
  //       name: "asklndkasjh",
  //       fields: [],
  //       open: true,
  //     },
  //   ]);

  const apiToken = props.location.state || null;
  const URL = "https://qa.manna-irrigation.com:8443/omer/api/v2/farms";
  console.log(apiToken);

  useEffect(() => {
    if (!apiToken) {
      props.history.push("/");
    } else {
      fetchFarms(apiToken.apiToken);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const fetchFarms = async (token) => {
    try {
      console.log(token);
      const myHeaders = new Headers({
        "Content-Type": "application/json",
        "X-User-Api-Token": token,
      });
      let response = await fetch(URL, {
        method: "GET",
        headers: myHeaders,
      });
      // .then((response) => response.json);
      if (response.status === 200) {
        let data = await response.json();
        console.log(data);
        setFarmsList(data);
      }
    } catch (e) {
      console.log(e);
      console.log("error at FarmList while attempting to fetch");
    }
  };

  //   function toggleFarmDropdown(nameid) {
  //     let farmsListCopy = [...farmsList];
  //     for (let i = 0; i < farmsListCopy.length; i++) {
  //       if (farmsListCopy[i].name === nameid) {
  //         let farmCopy = {
  //           open: !farmsListCopy[i].open,
  //           name: farmsListCopy[i].name,
  //           fields: farmsListCopy[i].fields,
  //         };
  //         farmsListCopy[i] = farmCopy;
  //         setFarmsList([...farmsListCopy]);
  //       }
  //     }
  //   }

  return (
    <div className="farmList">
      <Navbar />
      <h2>List of Farms</h2>
      {farmsList.length > 0 ? (
        <div className="farmList-list">
          {farmsList.map((farm, i) => (
            <div>
              <Farm
                farmid={farm.id}
                key={farm.id}
                token={apiToken.apiToken}
                name={farm.name}
                area={farm.area}
                //   open={farm.open}
                creationDate={farm.creation_date}
              />
              <br />
            </div>
          ))}
        </div>
      ) : (
        <image src="../assets/lind.gif"></image>
      )}
    </div>
  );
}

export default FarmList;
