import React, {useState, useEffect} from "react";
import {Link} from "react-router-dom";
import Navbar from "./Navbar";
import "./FarmList.css";
import Farm from "./Farm";

function FarmList(props) {
useEffect()
  const apiToken = props.location.state;
  console.log(state);
  const [farmsList, setFarmsList] = useState([
    {
      name: "amjad",
      fields: [123123, 123123, "sdfsdfsdfsd", "test,", "testasd"],
      open: true,
    },
    {
      name: "asdas",
      fields: ["test", "12s"],
      open: true,
    },
    {
      name: "asklndkasjh",
      fields: [],
      open: true,
    },
  ]);

  function toggleFarm(nameid) {
    // considerrrrr refaaccctooorriiinnnggg this shit
    let farmsListCopy = [...farmsList];
    for (let i = 0; i < farmsListCopy.length; i++) {
      if (farmsListCopy[i].name == nameid) {
        let farmCopy = {
          open: !farmsListCopy[i].open,
          name: farmsListCopy[i].name,
          fields: farmsListCopy[i].fields,
        };
        farmsListCopy[i] = farmCopy;
        setFarmsList([...farmsListCopy]);
      }
    }
  }

  return (
    <div className="farmList">
      <Navbar />
      <h2>List of Farms</h2>
      <div className="farmList-list">
        {farmsList.map((farm, i) => (
          <div>
            <Farm
              key={i}
              nameid={farm.name}
              fields={farm.fields}
              open={farm.open}
              toggleFunc={toggleFarm}
            />
            <br />
          </div>
        ))}
      </div>
    </div>
  );
}

export default FarmList;
